import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {connect} from 'react-redux';
import PropTypes from 'prop-types';
import {fetchArticles, setCurrArticle} from '../actions/articleActions';
import $ from 'jquery';

import './Article.css'; 

class Article extends Component {
   componentDidMount() {
      this.props.fetchArticles();
   }

   btnClick(e){
 //     $(".list-group-item").click(function(e){
         console.log(e);
         //this.props.setCurrArticle($(this).one().val());
 //     });
   }

   render() {
      const articleItems = this.props.articles.map(article => (
         <button key={article.id} data-value={article.id} type="button" className="list-group-item list-group-item-action articleButtons" onClick={this.btnClick}>
            <h5> {article.title} </h5>
            <p> {article.body} </p>
         </button>
      ));

      return (
         <div>
            <ul className="list-group">
            {articleItems}
            </ul>
         </div>
      )
   }
}

Article.propTypes = {
   fetchArticles: PropTypes.func.isRequired,  
   articles: PropTypes.array.isRequired
}

const mapStateToProps = state => ({
   articles: state.articles.items
})

export default connect(mapStateToProps, {fetchArticles, setCurrArticle})(Article);
