import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {connect} from 'react-redux';
import PropTypes from 'prop-types';
import {fetchArticles, setCurrArticle} from '../actions/articleActions';
import $ from 'jquery';

import './Article.css'; 

class Article extends Component {
   componentDidMount() {
      this.props.fetchArticles();
   }

   btnClick(e){
      console.log($(this).key);
     /* $('.list-group-item').click(function() {
         this.props.setCurrArticle($(this).key);
      });*/
   }

   render() {
      const articleItems = this.props.articles.map(article => (
         <button key={article.id} type="button" class="list-group-item list-group-item-action articleButtons" onClick={this.btnClick}>
            <h5> {article.title} </h5>
            <p> {article.body} </p>
         </button>
      ));

      return (
         <div>
            <ul class="list-group">
            {articleItems}
            </ul>
         </div>
      )
   }
}

Article.propTypes = {
   fetchArticles: PropTypes.func.isRequired,  
   articles: PropTypes.array.isRequired
}

const mapStateToProps = state => ({
   articles: state.articles.items
})

export default connect(mapStateToProps, {fetchArticles})(Article);
//export default connect(, {setCurrArticle})(Article);
