import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {connect} from 'react-redux';
//import {setCurrArticle} from '../actions/postActions.js';



class ArticleDisplay extends Component {
   componentDidMount() {
   }

   render() {
      return (
         <div className="card">
            <div className="card-body">
               <h1> Hello World </h1>
               <p> {this.props.articles[3].title} </p>
            </div>
         </div>
      )
   }
}

const maptStateToProps = state => ({
   currID: state.articles.currentID,
   articles: state.articles.items
})

export default connect(maptStateToProps, {}) (ArticleDisplay);
