import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {connect} from 'react-redux';
import {fetchArticles} from '../actions/articleActions.js';



class ArticleDisplay extends Component {
   componentDidMount() {
      this.props.fetchArticles();
      console.log(this.props.articles);
   }

   render() {
const articleItems = this.props.articles.map(article => (
         <button key={article.id} id={article.id} type="button" className="list-group-item list-group-item-action articleButtons" onClick={this.btnClick.bind(this)}>
            <h5> {article.title} </h5>
            <p> {article.body} </p>
         </button>
      ));

      return (
         <div className="card">
            <div className="card-body">
               <h1> Hello World </h1>
               <p> {} </p>
            </div>
         </div>
      )
   }
}

const maptStateToProps = state => ({
   currID: state.articles.currentID,
   articles: state.articles.items
})

export default connect(maptStateToProps, {fetchArticles}) (ArticleDisplay);
