import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {connect} from 'react-redux';
import {fetchArticles} from '../actions/postActions';


class Article extends Component {
   componentDidMount() {
      this.props.fetchArticles();
   }


   render() {
      const articleItems = this.props.articles.map(article => (
         <button key={article.id} type="button" class="list-group-item list-group-item-action articleButtons" style={{margin: 10, opacity:0.5}}>
            <h5> {article.title} </h5>
            <p> {article.body} </p>
         </button>
      ));

      return (
         <div>
            <ul class="list-group">
            {articleItems}
            </ul>
         </div>
      )
   }
}

const mapStateToProps = state => ({
   articles: state.articles.items
})

export default connect(mapStateToProps, {fetchArticles})(Article);
